sys_variable_value.value:
(function execute(inputs, outputs) {
  var returnCode;
  var body = sanitizeBodyData();
  var key = new global.ScopedEncrypter().decrypt(inputs.pingIntegrationRecord.api_key);
  try {
    var restMessage = new sn_ws.RESTMessageV2();
    restMessage.setHttpMethod("post");
    restMessage.setEndpoint(inputs.pingIntegrationRecord.rest_endpoint);
    restMessage.setRequestHeader("Content-Type", "application/json");
    restMessage.setRequestHeader("Authorization", "Bearer " + key);
    restMessage.setRequestBody(JSON.stringify(body));
    var response = restMessage.execute();
    returnCode = response.getStatusCode();
  } catch(error) {
    gs.info(error);
    returnCode = 500;
  }

  function sanitizeBodyData() {
    var jsonBody = {"number": inputs.recordNumber};
    for (var i=0; i<inputs.requestBody.length; i++) {
      jsonBody[inputs.requestBody[i]["field_name"]] = {
        "prev_value": inputs.requestBody[i]["previous_display_value"],
        "new_value": inputs.requestBody[i]["current_display_value"]
      }
    }
    return jsonBody;
  }

})(inputs, outputs);

