sys_variable_value.value:
(function execute(inputs, outputs) {

  if (!inputs.api_key) {
    return;
  }

  var credSysId = "";
  if (checkForExistingCredentials()) {
      updateExistingRecord();
  } else {
      createNewCredentialRecord();
  }

  outputs.credSysId = credSysId;

  function createNewCredentialRecord() {
    var cred = new GlideRecord("discovery_credentials");
    cred.initialize();
    cred.name = "Ping Integration - " + inputs.assignment_group_name;
    cred.api_key = inputs.api_key;
    credSysId = cred.sys_id;
    cred.insert();
  }

  function updateExistingRecord() {
    var cred = new GlideRecord("discovery_credentials");
    cred.addQuery("name", "Ping Integration - " + inputs.assignment_group_name);
    cred.query();
    if (cred.next()) {
      cred.api_key = inputs.api_key;
      credSysId = cred.sys_id;
      cred.update();
    }
  }

  function checkForExistingCredentials() {
    var cred = new GlideRecord("discovery_credentials");
    return cred.get("name", "Ping Integration - " + inputs.assignment_group_name);
  }
})(inputs, outputs);

