sys_variable_value.value:
(function execute(inputs, outputs) {
  var restSysId = "";

  try {

    if (checkForExistingRecord()) {
      updateExistingRecord();
    } 
    else {
      createNewRestRecord();
      createNewFunction();
    }

    outputs.restMessageSysId = restSysId;

    function createNewRestRecord() {
      var rm = new GlideRecord("sys_rest_message");
      rm.initialize();
      rm.name = "Ping Integration - " + inputs.assignment_group_name;
      rm.rest_endpoint = inputs.endpoint;
      restSysId = rm.sys_id;
      rm.insert();
    }

    function createNewFunction() {
      var rmFn = new GlideRecord("sys_rest_message_fn");
      rmFn.initialize();
      rmFn.function_name = "POST";
      rmFn.http_method = "post";
      rmFn.rest_endpoint = inputs.endpoint;
      rmFn.rest_message = restSysId;
      rmFn.insert();
    }

    function updateExistingRecord() {
      var rm = new GlideRecord("sys_rest_message");
      rm.addQuery("name", "Ping Integration - " + inputs.assignment_group_name);
      if (rm.next()){
        rm.rest_endpoint = inputs.endpoint;
        restSysId = rm.sys_id;
        rm.update();
      }
    }

    function updateExistingMethod() {
      var rmFn = new GlideRecord("sys_rest_message_fn");
      rmFn.addQuery("rest_message", restSysId);
      rmFn.addQuery("function_name", "POST");
      rmFn.query();
      if (rmFn.next()) {
        rmFn.rest_endpoint = inputs.endpoint;
        rmFn.update();
      }
    }

    function checkForExistingRecord() {
      var rm = new GlideRecord("sys_rest_message");
      return rm.get("name", "Ping Integration - " + inputs.assignment_group_name);
    }
  } catch (error) {
    outputs.error = error;
  }  


})(inputs, outputs);

